#include <stdio.h>
#include <string.h>
#include <stdlib.h>

char *input="";
//char *input="nppdvjthqldpwncqszvftbrmjlhg";


int findStart(char *input, int uniques) {
	int i;
	for(i=0; input[i+(uniques-1)]; i++) {
		int match=0;
		for(int j=i; j<i+uniques && !match; j++) {
			for(int k=j+1; k<i+uniques && !match; k++) {
				if(input[j]==input[k]) 
				match=1;
			}
		}
		if(!match) {
			printf("First %d marker after char %i\n", uniques, i+uniques);
			break;
		}

	}
	return i+uniques;
}

int main(int argc, char *argv[]) {
	printf("Testing 'mjqjpqmgbljsphdztnvjfqwrcgsmlb'\n");
	findStart("mjqjpqmgbljsphdztnvjfqwrcgsmlb", 4);
	findStart("mjqjpqmgbljsphdztnvjfqwrcgsmlb", 14);

	findStart(input, 4);
	findStart(input, 14);
	printf("Done\n");
}